mapper.select:
select

from t_mds_order
where
1 = 1

  and  order_placer_type = #{orderPlacerType}


  and  order_type = #{orderType}


  and  store_id = #{storeId}


  and  order_status = #{orderStatus}


  and  order_status = #{orderStatus}


  and  create_time >= #{startTime}


  and   #{endTime} >= create_time


  and  id = #{id}


  and  order_code = #{orderCode}

order by create_time

select count(0) from t_mds_order where store_id = #{storeId} and order_status = 7
and create_time >= #{startTime}
and #{endTime} >= create_time
and physical_store_id in
#{item}

select count(0) from t_mds_order where store_id = #{storeId} and order_status = 7 and order_type = 1
and create_time >= #{startTime}
and #{endTime} >= create_time
and physical_store_id in
#{item}

select ifnull(sum(pay_amount),0) from t_mds_order where store_id = #{storeId} and order_status = 7 and order_type = 1
and create_time >= #{startTime}
and #{endTime} >= create_time
and physical_store_id in
#{item}

